	private void readObject(java.io.ObjectInputStream stream) throws java.io.IOException, java.lang.ClassNotFoundException{
		try{
			production = stream.readInt();
			trade = stream.readInt();
			culture = stream.readInt();
			settler = stream.readInt();
			army = stream.readInt();
			village = stream.readInt();
			hut = stream.readInt();
			xPos = stream.readInt();
			yPos = stream.readInt();
			buildingNum = stream.readInt();
			cityIndex = stream.readInt();
			gold = stream.readInt();
			villageResource = stream.readUTF();
			hutResource = stream.readUTF();
			isMapBorder = stream.readBoolean();
			hasBuild = stream.readBoolean();
			isCitySpace = stream.readBoolean();
			owner = (Player)stream.readObject();
			terrain = stream.readUTF();
			resource = stream.readUTF();
			holder = (GameJLayeredPane)stream.readObject();
			icon = (GameImageIcon)stream.readObject();
			buildingIcon = (GameImageIcon)stream.readObject();
			buildingLabel = (GameJLabel)stream.readObject();
      }catch(java.io.IOException ioEx){
			System.out.println("IO EXCEPTION getSentObject\n");
			ioEx.printStackTrace();
		}
   }

   private void writeObject(java.io.ObjectOutputStream stream) throws java.io.IOException{
		try{
//System.out.println("production " +production);
			stream.writeInt(production);
//System.out.println("trade "+trade);
			stream.writeInt(trade);
//System.out.println("culture "+culture);
			stream.writeInt(culture);
//System.out.println("settler "+settler);
			stream.writeInt(settler);
//System.out.println("army "+army);
			stream.writeInt(army);
//System.out.println("village "+village);
			stream.writeInt(village);
//System.out.println("hut "+hut);
			stream.writeInt(hut);
//System.out.println("xPos "+xPos);
			stream.writeInt(xPos);
//System.out.println("yPos "+yPos);
			stream.writeInt(yPos);
//System.out.println("buildingNum "+buildingNum);
			stream.writeInt(buildingNum);
//System.out.println("cityIndex "+cityIndex);
			stream.writeInt(cityIndex);
//System.out.println("gold "+gold);
			stream.writeInt(gold);
//System.out.println("villageResource "+villageResource);
			stream.writeUTF(villageResource);
//System.out.println("hutResource "+hutResource);
			stream.writeUTF(hutResource);
//System.out.println("isMapBorder "+isMapBorder);
			stream.writeBoolean(isMapBorder);
//System.out.println("hasBuild "+hasBuild);
			stream.writeBoolean(hasBuild);
//System.out.println("isCitySpace "+isCitySpace);
			stream.writeBoolean(isCitySpace);
//System.out.println("owner "+owner);
			stream.writeObject(owner);
//System.out.println("terrain "+terrain);
			stream.writeUTF(terrain);
//System.out.println("resource "+resource);
			stream.writeUTF(resource);
//System.out.println("holder "+holder);
			stream.writeObject(holder);
//System.out.println("icon "+icon);
			stream.writeObject(icon);
//System.out.println("buildingIcon "+buildingIcon);
			stream.writeObject(buildingIcon);
//System.out.println("buildingLabel "+buildingLabel);
			stream.writeObject(buildingLabel);
		}
		catch(java.net.ConnectException conEx){
			System.out.println("Connection Refused!\n");
			conEx.printStackTrace();
			System.exit(1);
		}
		catch(java.io.NotSerializableException NSEx){
			System.out.println("NOT SERIALIZABLE\n");
			NSEx.printStackTrace();
		}
		catch(java.io.IOException ioEx){
			System.out.println("IO EXCEPTION sendGameCommand\n");
			ioEx.printStackTrace();
		}
	
	}
 
